import React from 'react';

class Searchbar extends React.Component {

    state = {
        term: 'Default text',
        typing: false,
        timeout : 0
    };
    // handleChange = (event) => {
    //     this.setState({
    //         term: event.target.value
    //     });
    // };

    handleChange = (event) => {
        const self = this;

        if (self.state.timeout) {
           clearTimeout(self.state.timeout);
        }

        self.setState({
           term: event.target.value,
           typing: false,
           timeout: setTimeout(() => {
               self.props.handleFormSubmit(self.state.term);
             }, 2500)
        });
    }

    handleSubmitButton = (event) => {
        event.preventDefault();
        this.props.handleFormSubmit(this.state.term);
    }

    // handleSubmit = event => {
    //     event.preventDefault();
    //     this.props.handleFormSubmit(this.state.term);
    // }

    render() {
        return (
            <div className='search-bar ui segment'>
                <form onSubmit={this.handleSubmit} className='ui form'>
                    <div className='field'>
                        <label htmlFor="video-search">Video Search</label>
                        <input onChange={this.handleChange.bind(this)} name='video-search' type="text" value={this.state.term}/>
                        <button onClick={this.handleSubmitButton.bind(this)} type="submit">SUBMIT</button>
                    </div>
                </form>
            </div>
        )
    }
}
export default Searchbar;
